using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.AspNetCore.Cors;using Microsoft.AspNetCore.Mvc;using VacationHireInc.Business.Abstractions;using VacationHireInc.Business.Services.GeneralDtos;namespace VacationHireInc.Api.Controllers{    [ApiController]    [Route("[controller]")]    [EnableCors("AllowAngular")]    [ApiExplorerSettings(GroupName = "VacationHire")]    public class CurrencyController : ControllerBase    {        private readonly ICurrencyService _service;        public CurrencyController(ICurrencyService service)        {            _service = service;        }        [HttpGet]        public async Task<List<OptionDto>> GetListAsync()        {            return await _service.GetListAsync();        }        [HttpGet("convert/{amount:double}/{currencyForConvert}")]        public async Task<double> GetConvertedPriceAsync(double amount, string currencyForConvert)        {            return await _service.ConvertCurrencyAsync(amount, currencyForConvert);        }    }}