using System;using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.AspNetCore.Cors;using Microsoft.AspNetCore.Mvc;using VacationHireInc.Business.Abstractions;using VacationHireInc.Business.Services.Customer.Dtos;namespace VacationHireInc.Api.Controllers{    [ApiController]    [Route("[controller]")]    [EnableCors("AllowAngular")]    [ApiExplorerSettings(GroupName = "VacationHire")]    public class CustomerController : ControllerBase    {        private readonly ICustomerService _service;        public CustomerController(ICustomerService service)        {            _service = service;        }        [HttpGet]        public async Task<List<CustomerDto>> GetListAsync()        {            return await _service.GetListAsync();        }        [HttpGet("{id:guid}")]        public async Task<CustomerDto> GetDetailsAsync(Guid id)        {            return await _service.GetDetailsAsync(id);        }        [HttpPost]        public async Task PostAsync(CustomerDto dto)        {            await _service.CreateAsync(dto);        }        [HttpPut("{id:guid}")]        public async Task PutAsync(Guid id, [FromBody] CustomerDto dto)        {            await _service.UpdateAsync(id, dto);        }        [HttpDelete("{id:guid}")]        public async Task DeleteAsync(Guid id)        {            await _service.DeleteAsync(id);        }    }}