using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.AspNetCore.Cors;using Microsoft.AspNetCore.Mvc;using VacationHireInc.Business.Abstractions;using VacationHireInc.Business.Services.ReceptionCard.Dtos;namespace VacationHireInc.Api.Controllers{    [ApiController]    [Route("[controller]")]    [EnableCors("AllowAngular")]    [ApiExplorerSettings(GroupName = "VacationHire")]    public class ReceptionCardController : ControllerBase    {        private readonly IReceptionCardService _service;        public ReceptionCardController(IReceptionCardService service)        {            _service = service;        }        [HttpGet]        public async Task<List<ReceptionCardDto>> GetListAsync()        {            return await _service.GetListAsync();        }        [HttpGet("{id}")]        public async Task<ReceptionCardDto> GetDetailsAsync(string id)        {            return await _service.GetDetailsAsync(id);        }        [HttpPost]        public async Task PostAsync(ReceptionCardDto dto)        {            await _service.CreateAsync(dto);        }        [HttpPut("{id}")]        public async Task PutAsync(string id, [FromBody] ReceptionCardDto dto)        {            await _service.UpdateAsync(id, dto);        }        [HttpDelete("{id}")]        public async Task DeleteAsync(string id)        {            await _service.DeleteAsync(id);        }    }}