using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.AspNetCore.Cors;using Microsoft.AspNetCore.Mvc;using VacationHireInc.Business.Abstractions;using VacationHireInc.Business.Services.GeneralDtos;using VacationHireInc.Business.Services.Reservation.Dtos;namespace VacationHireInc.Api.Controllers{    [ApiController]    [Route("[controller]")]    [EnableCors("AllowAngular")]    [ApiExplorerSettings(GroupName = "VacationHire")]    public class ReservationController : ControllerBase    {        private readonly IReservationService _service;        public ReservationController(IReservationService service)        {            _service = service;        }        [HttpGet]        public async Task<List<ReservationDto>> GetListAsync()        {            return await _service.GetListAsync();        }        [HttpGet("{id}")]        public async Task<ReservationDto> GetDetailsAsync(string id)        {            return await _service.GetDetailsAsync(id);        }        [HttpPost]        public async Task PostAsync(ReservationDto dto)        {            await _service.CreateAsync(dto);        }        [HttpPut("{id}")]        public async Task PutAsync(string id, [FromBody] ReservationDto dto)        {            await _service.UpdateAsync(id, dto);        }        [HttpDelete("{id}")]        public async Task DeleteAsync(string id)        {            await _service.DeleteAsync(id);        }        [HttpGet("confirmed")]        public async Task<List<OptionDto>> GetConfirmedReservationListAsync()        {            return await _service.GetConfirmedReservationListAsync();        }        [HttpGet("{id}/total-invoice")]        public async Task<decimal> GetTotalInvoiceAsync(string id)        {            return await _service.CalculateTotalInvoiceAsync(id);        }    }}