using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.AspNetCore.Cors;using Microsoft.AspNetCore.Mvc;using VacationHireInc.Business.Abstractions;using VacationHireInc.Business.Services.GeneralDtos;using VacationHireInc.Business.Services.Vehicle.Dtos;namespace VacationHireInc.Api.Controllers{    [ApiController]    [Route("[controller]")]    [EnableCors("AllowAngular")]    [ApiExplorerSettings(GroupName = "VacationHire")]    public class VehicleController : ControllerBase    {        private readonly IVehicleService _service;        public VehicleController(IVehicleService service)        {            _service = service;        }        [HttpGet]        public async Task<List<VehicleDto>> GetListAsync()        {            return await _service.GetListAsync();        }        [HttpGet("{id}")]        public async Task<VehicleDto> GetDetailsAsync(string id)        {            return await _service.GetDetailsAsync(id);        }        [HttpPost]        public async Task PostAsync(VehicleDto dto)        {            await _service.CreateAsync(dto);        }        [HttpPut("{id}")]        public async Task PutAsync(string id, [FromBody] VehicleDto dto)        {            await _service.UpdateAsync(id, dto);        }        [HttpDelete("{id}")]        public async Task DeleteAsync(string id)        {            await _service.DeleteAsync(id);        }        [HttpGet("disposable")]        public async Task<List<OptionDto>> GetDisposableVehicleAsync()        {            return await _service.GetDisposableVehicleListAsync();        }    }}