using Microsoft.AspNetCore.Builder;using Microsoft.AspNetCore.Hosting;using Microsoft.Extensions.Configuration;using Microsoft.Extensions.DependencyInjection;using Microsoft.Extensions.Hosting;using Microsoft.OpenApi.Models;using VacationHireInc.Business.Extensions;namespace VacationHireInc.Api{    public class Startup    {        private readonly IConfiguration _configuration;        public Startup(IConfiguration configuration)        {            Configuration = configuration;            _configuration = configuration;        }        public IConfiguration Configuration { get; }        // This method gets called by the runtime. Use this method to add services to the container.        public void ConfigureServices(IServiceCollection services)        {            services.AddControllers().AddNewtonsoftJson();            services.AddSwaggerGen(options =>            {                options.SwaggerDoc("VacationHire", new OpenApiInfo {Version = "v1", Title = "Vacation Hire INC API"});            });            services.AddService(_configuration);            services.AddCors(options =>            {                options.AddPolicy("AllowAngular", p =>                    p.WithOrigins("http://localhost:4200")                        .AllowAnyMethod()                        .AllowAnyHeader());            });        }        // This method gets called by the runtime. Use this method to configure the HTTP request pipeline.        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)        {            if (env.IsDevelopment())            {                app.UseDeveloperExceptionPage();                app.UseSwagger();                app.UseSwaggerUI(options => { options.SwaggerEndpoint("/swagger/VacationHire/swagger.json", "Vacation Hire Inc - v1"); });            }            app.UseHttpsRedirection();            app.UseRouting();            app.UseAuthorization();            app.UseCors();            app.UseEndpoints(endpoints => { endpoints.MapControllers(); });        }    }}