using System;using System.Net.Http;using Microsoft.Extensions.Configuration;namespace VacationHireInc.Business.Extensions{    public static class HttpClientExtension    {        public static void ConfigureClientWithApiKey(this HttpClient httpClient, IConfiguration configuration, string clientName)        {            httpClient.BaseAddress = new Uri(configuration.GetValue<string>($"Http:{clientName}:BaseUri"));            httpClient.DefaultRequestHeaders.Add(configuration.GetValue<string>($"Http:{clientName}:Headers:Apikey:Name"),                configuration.GetValue<string>($"Http:{clientName}:Headers:Apikey:Value"));        }    }}