using System;using Newtonsoft.Json;using Newtonsoft.Json.Converters;using VacationHireInc.Data.Entities.Enums;namespace VacationHireInc.Business.Services.Vehicle.Dtos{    public class VehicleDto    {        public string Id { get; set; }        public string Name { get; set; }        public DateTime CreateDate { get; set; }        public string Color { get; set; }        public decimal NumberOfMiles { get; set; }        public decimal Price { get; set; }        [JsonConverter(typeof(StringEnumConverter))]        public AvailabilityStatus AvailabilityStatus { get; set; }        [JsonConverter(typeof(StringEnumConverter))]        public VehicleBodyType BodyType { get; set; }        [JsonConverter(typeof(StringEnumConverter))]        public VehicleBrandType BrandType { get; set; }        [JsonConverter(typeof(StringEnumConverter))]        public VehicleFuelType FuelType { get; set; }        public string ReservationId { get; set; }    }}