using System;using System.Linq;using System.Threading.Tasks;using Microsoft.EntityFrameworkCore.Storage;namespace VacationHireInc.Data.Abstraction{    public interface IRepository    {        IQueryable<TEntity> Query<TEntity>() where TEntity : class;        Task InsertAsync<TEntity>(TEntity entity) where TEntity : class;        Task SaveAsync();        Task ExecuteTransactionalAsync(Func<IDbContextTransaction, Task> action);    }}