using System.Linq;using System.Threading.Tasks;namespace VacationHireInc.Data.Abstraction{    public interface IRepository    {        /// <summary>        ///     Gets the query on the database entities.        /// </summary>        /// <returns>The query.</returns>        IQueryable<TEntity> Query<TEntity>() where TEntity : class;        Task InsertAsync<TEntity>(TEntity entity) where TEntity : class;        Task SaveAsync();    }}