using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using VacationHireInc.Data.Entities;namespace VacationHireInc.Data.FluentDbConfigs{    public class ReceptionCardConfig : IEntityTypeConfiguration<ReceptionCard>    {        public void Configure(EntityTypeBuilder<ReceptionCard> builder)        {            builder.ToTable("ReceptionCards", Constants.DbSchemaName);            builder.HasIndex(e => e.Id);            builder.Property(e => e.Id).HasMaxLength(36).IsRequired();            builder.Property(e => e.ObjectCondition).IsRequired();            builder.Property(e => e.ObjectType).IsRequired();            builder.Property(e => e.OccuredAt).IsRequired();            builder.Property(e => e.Currency).IsRequired();            builder.Property(e => e.TotalInvoice).IsRequired();        }    }}