using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using VacationHireInc.Data.Entities;namespace VacationHireInc.Data.FluentDbConfigs{    public class VehicleConfig : IEntityTypeConfiguration<Vehicle>    {        public void Configure(EntityTypeBuilder<Vehicle> builder)        {            builder.ToTable("Vehicles", Constants.DbSchemaName);            builder.HasIndex(e => e.Id);            builder.Property(e => e.Id).HasMaxLength(36).IsRequired();            builder.Property(e => e.Name).IsRequired();            builder.Property(e => e.Color).IsRequired();            builder.Property(e => e.NumberOfMiles).IsRequired();            builder.Property(e => e.Price).IsRequired();            builder.Property(e => e.AvailabilityStatus).IsRequired();            builder.Property(e => e.BodyType).IsRequired();            builder.Property(e => e.BrandType).IsRequired();            builder.Property(e => e.FuelType).IsRequired();            builder.HasOne(e => e.Reservation).WithMany(e => e.Vehicles)                .HasForeignKey(e => e.ReservationId);        }    }}