using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Design;namespace VacationHireInc.Data.Migrations{    public class VacationHireContextFactory : IDesignTimeDbContextFactory<VacationHireContext>    {        private const string ConnectionString = "Host=localhost;Database=postgres;Username=postgres;Password=aimer18";        public VacationHireContext CreateDbContext(string[] args)        {            var optionsBuilder = new DbContextOptionsBuilder<VacationHireContext>();            optionsBuilder.UseNpgsql(ConnectionString);            return new VacationHireContext(optionsBuilder.Options);        }    }}