using System.Linq;using System.Threading.Tasks;using VacationHireInc.Data.Abstraction;namespace VacationHireInc.Data{    public sealed class Repository : IRepository    {        private readonly VacationHireContext _context;        public Repository(VacationHireContext context)        {            _context = context;        }        public IQueryable<TEntity> Query<TEntity>() where TEntity : class        {            return _context.Set<TEntity>();        }        public async Task InsertAsync<TEntity>(TEntity entity) where TEntity : class        {            await _context.Set<TEntity>().AddAsync(entity);        }        public async Task SaveAsync()        {            await _context.SaveChangesAsync();        }    }}