using Microsoft.EntityFrameworkCore;using VacationHireInc.Data.Entities;namespace VacationHireInc.Data{    public class VacationHireContext : DbContext    {        public VacationHireContext(DbContextOptions<VacationHireContext> options)            : base(options)        {        }        public DbSet<Customer> Customers { get; set; }        public DbSet<ReceptionCard> ReceptionCards { get; set; }        public DbSet<Reservation> Reservations { get; set; }        public DbSet<Vehicle> Vehicles { get; set; }    }}